import {getfeed, likepost} from "../../types";

const initialState = {
    postitems: [],
    new_liked_posts : [],
};


const PostsReducer = (state = initialState, action) => {
    switch (action.type) {
        case getfeed: {
            return {
            ...state,
            postitems: action.payload,
            new_liked_posts: [],
    }}
        case likepost: {
            return {
                ...state,
                new_liked_posts: action.payload
            }
        }
        default: {
            return state
        }
    }

}
export default PostsReducer;
