import {friendfriend} from "../../types";

export const friendFriendAction = () => ({
    type: friendfriend,
});

const followFriendFunction = (props) => (dispatch) => {  
    const URL = "https://tracey.propulsion-learn.ch/backend/api/social/friends/request/" + props.friend.id + "/";

    const token = props.props.SignInReducer.token;
    const headers = new Headers({
        'Content-Type': 'application/json',
        'Accept': 'application/json',
        'Authorization': `Bearer ${token}`
    });

    const config = {
        method: 'POST',
        headers,
    };

    fetch(URL, config)
    dispatch(friendFriendAction());

};
export default friendFriendFunction;
