import {verify,verifyerror} from "../../types";

export const verifyAction = () => ({
    type: verify,
});

export const verifyError = () => ({
    type: verifyerror
})

const URL = `https://motion.propulsion-home.ch/backend/api/auth/registration/validation/`;

export const verifyFunction = (data) => (dispatch) => {
    const config = {
        method: 'PATCH',
        body: JSON.stringify(data),
        headers: {'Content-Type': 'application/json'}
    };

    fetch(URL, config)
        .then(function(response) {
            if (response.ok) {
                dispatch(verifyAction());
            } 
            else {
                dispatch(verifyError());
                response.json().then(function(object) {
                const errorArray = Object.values(object);
                let errorStr = '';   
                for (let i=0; i <errorArray.length; i++) {
                    errorStr = errorStr + "\r\n " + errorArray[i]; 
                };
                alert("Error: "+ errorStr)

            })
            }
                })
    }
