import {getfeed} from "../../types";

export const getFilteredPostsAction = content => ({
    type: getfeed,
    payload: content
});


const URL = `https://tracey.propulsion-learn.ch/backend/api/social/posts/`;

const getFilteredPostsFunction = (props) => (dispatch) => {
    console.log('in func', props);
    console.log(props.FeedReducer.currentFeedState)

    const token = props.SignInReducer.token
    const headers = new Headers({
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
    });

    const config = {
        method: 'GET',
        headers
    };

    fetch(URL, config)
    .then(res => res.json())
    .then(data => {
        const postsList = data;
        dispatch(getFilteredPostsAction(postsList));
    })
};
export default getFilteredPostsFunction;
