import {showheadermenu} from "../../types";

const initialState = {
    showheadermenu: false,
};


const HeaderReducer = (state = initialState, action) => {
    switch (action.type) {
        case showheadermenu: {
            return {
             ...state,
             showheadermenu: !state.showheadermenu, 
     }}
        default: {
            return state
        }
    } 
}
export default HeaderReducer;
