import {likepost} from "../../types";

export const LikePostAction = (current_new_posts) => ({
    type: likepost,
    payload: current_new_posts,
});

const LikePostFunction = (props,post_id,new_post) => (dispatch) => {  
    const URL = "https://motion.propulsion-home.ch/backend/api/social/posts/toggle-like/" + post_id + "/";
    const current_new_posts = props.PostsReducer.new_liked_posts
    current_new_posts.push(new_post);
    const token = props.props.SignInReducer.token;
    const headers = new Headers({
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
    }); 

    const config = {
        method: 'POST',
        headers,
        body: {images:[],shared:{},content:''}
    };

    fetch(URL, config);
    dispatch(LikePostAction(current_new_posts));

};
export default LikePostFunction;
