import {getfeed, likepost} from "../../types";

const initialState = {
    postitems: [],
    isLoaded: false,
};


const PostsReducer = (state = initialState, action) => {
    switch (action.type) {
        case getfeed: {
            return {
            ...state,
            postitems: action.payload,
            has_loaded: true,
    }}
        case likepost: {
            return {
                ...state,
            }
        }
        default: {
            return state
        }
    }

}
export default PostsReducer;
