import React,{useState} from 'react';
import './index.css';
import {connect} from 'react-redux';
import {Link} from 'react-router-dom';
import TimeAgo from 'react-timeago'
import englishStrings from 'react-timeago/lib/language-strings/en'
import buildFormatter from 'react-timeago/lib/formatters/buildFormatter'
import FeedLikeButton from '../FeedLikeButton';

const formatter = buildFormatter(englishStrings)

function FeedFeedFollow(props) {
    const [followed_posts, setFollowedPosts] = useState();

    const posts = props.PostsReducer;

        if (posts.postitems.length > 0 && followed_posts === undefined) {
            const currentFollowedPosts = posts.postitems.filter(post => post.user.logged_in_user_is_following);
            setFollowedPosts(currentFollowedPosts);

        }

    const updateLikedPosts = (post_id) => {
        const newFollowedPosts = followed_posts.filter(post => post.id !== parseInt(post_id));
        setFollowedPosts(newFollowedPosts);
    }

    if(followed_posts === undefined ){
        return (
            <div>Loading ...</div>
                ) 
        } else if (followed_posts.length === 0) {
            return(
                    <div className="posts_like_feed">
                    <div className="post_like_container">
                    <div className="follow_empty">
                        <p>It looks like no one you're following has posted yet..</p>
                        <p>Want to connect with more people?</p>
                        <button id="go_get_followers"><Link to="/friends">Find Friends</Link></button>
                    </div>
                    </div>
                </div>            )
        }

        else {
            return (
                <div className="posts_like_feed">
                    <div className="post_like_container">
                    {followed_posts.map(post => 
                        <div className="post_container" id={post.id} key={post.id} >
                            <div className="post_header">
                                {post.user.avatar ? <img src={post.user.avatar} alt={post.user.avatar}/> :<img src={require('../../assets/images/blank_avatar.png')} alt={"avatar"}/>}    
                                <div className="post_name"> 
                                    {post.user.first_name} {post.user.last_name}
                                    <TimeAgo date={post.created} formatter={formatter} />
                                </div>
                            </div>
                            <div className="post_content" onClick={(e) => props.fullPostHandler(e,post)}>
                                {post.content}
                                </div>

                                <div className="post_images" onClick={(e) => props.fullPostHandler(e,post)}>
                                    {post.images[0] ?  <img src={post.images[0].image} alt={post.images[0].image}/> : <div></div> } 
                                    {post.images[1] ?  <img src={post.images[1].image} alt={post.images[1].image}/> : <div></div> } 
                                    {post.images[2] ?  <img src={post.images[2].image} alt={post.images[2].image}/> : <div></div> } 
                                    {post.images[3] ?  <img src={post.images[3].image} alt={post.images[3].image}/> : <div></div> }             
                            </div>
                            <div className="post_footer">
                                <FeedLikeButton 
                                props={props}
                                post={post}
                                updateLikedPosts={updateLikedPosts}
                                />
                            </div>
                        </div>)}
                </div>
                </div>
            )}   
    }


const mapStateToProps = state => {
    return {
        ...state,
      };
    };   

export default connect(mapStateToProps)(FeedFeedFollow);
